import ListHeader from './components/ListHeader'
import Auth from './components/Auth'
import { useEffect, useState } from 'react'
import ListItem from './components/ListItem'
import { useCookies } from 'react-cookie'

const App = () => {
  const [cookies, setCookie, removeCookie] = useCookies(null)
  const authToken = cookies.AuthToken
  const userEmail = cookies.Email
  const [tasks, setTasks] = useState(null)

  const getData = async () => {
    try {
      console.log('oiiii', cookies)
      const response = await fetch(`http://localhost:8000/todos/${userEmail}`)
      const json = await response.json()

      setTasks(json)
    } catch (err) {
      console.error(err)
    }
  }

  useEffect(() => {
    if (authToken) {
      getData()
    }
  }, [])

  console.log(tasks)

  // Sort tasks by date
  const sortedTasks = tasks?.sort((a, b) => new Date(a.date) - new Date(b.date))

  const updateTaskDuration = async (taskId, newDuration) => {
    try {
      const response = await fetch(`http://localhost:8000/todos/${taskId}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${authToken}`,
        },
        body: JSON.stringify({ duration: newDuration }),
      })

      if (response.ok) {
        // Task duration updated successfully
        getData() // Refresh the tasks data
      } else {
        console.error('Failed to update task duration')
      }
    } catch (err) {
      console.error(err)
    }
  }

  return (
    <div className='app'>
      {!authToken && <Auth />}
      {authToken && (
        <>
          <ListHeader listName={'TO DO LIST'} getData={getData} />
          <p className='user-email'>HI! {userEmail} </p>
          {sortedTasks?.map((task) => (
            <ListItem
              key={task.id}
              task={task}
              getData={getData}
              updateTaskDuration={updateTaskDuration}
            />
          ))}
        </>
      )}
      <p className='copyright'>COPYLEFT Â© BY MNH</p>
    </div>
  )
}

export default App
