      <div className="overlay" >
        <div className="modal">
          <div className="form-title-container">
            <h3>FORM</h3>
            <button onClick={()=>setShowModal(false)}>X</button>
          </div>

      <form>
        <input 
        required
        maxLength={30}
        placeholder="YOUR TASK"
        name="title"
        value={data.title}
        onChange={handleChange}
        />
<br/>
<label for ="range">PROGRESS?</label>

        <input 
        required
        type="range"
        id="range"
        min="0"
        max="100"
        name="progress"
        value={data.progress}
        onChange={handleChange}/>
        <input className={mode} type="submit" value="UBAH" onClick={editMode ? editData: postData}
        />
      </form>


        </div>
      </div>
    );
  }

  export default Modal;
