import { useState, useEffect } from 'react';
import moment from 'moment';
import TickIcon from './TickIcon';
import ProgressBar from './ProgressBar';
import Modal from './Modal';

const ListItem = ({ task, getData }) => {
  const [showModal, setShowModal] = useState(false);
  const [formattedDate, setFormattedDate] = useState('');
  const [formattedDeadline, setFormattedDeadline] = useState('');

  useEffect(() => {
    const calculateTimeAgo = () => {
      const now = moment();
      const taskDate = moment(task.date);
      const deadline = moment(task.deadline);
      const duration = moment.duration(now.diff(taskDate));
      const deadlineDuration = moment.duration(deadline.diff(now));

      const days = Math.floor(duration.asDays());
      const hours = duration.hours();

      let formatted = '';
      if (days > 0) {
        formatted += `${days} day${days > 1 ? 's' : ''}`;
      }
      if (hours > 0) {
        formatted += ` ${hours} hour${hours > 1 ? 's' : ''}`;
      }

      if (formatted === '') {
        formatted = 'Just now';
      } else {
        formatted += ' ago';
      }

      setFormattedDate(formatted);

      const deadlineDays = Math.floor(deadlineDuration.asDays());
      const deadlineHours = deadlineDuration.hours();

      let formattedDeadline = '';
      if (deadlineDays > 0) {
        formattedDeadline += `${deadlineDays} day${deadlineDays > 1 ? 's' : ''}`;
      }
      if (deadlineHours > 0) {
        formattedDeadline += ` ${deadlineHours} hour${deadlineHours > 1 ? 's' : ''}`;
      }

      setFormattedDeadline(formattedDeadline);
    };

    calculateTimeAgo();
  }, [task.date, task.deadline]);

  const deleteItem = async () => {
    try {
      const response = await fetch(`http://localhost:8000/todos/${task.id}`, {
        method: 'DELETE',
      });
      if (response.status === 200) {
        getData();
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <li className="list-item">
      <div className="info-container">
        <TickIcon />
        <p className="task-title">{task.title}</p>
        <ProgressBar progress={task.progress} />
        <p className="user-email">{formattedDate}</p>
        <p className="deadline">{formattedDeadline}</p>
      </div>
      <div className="button-container">
        <button className="edit" onClick={() => setShowModal(true)}>
          EDIT
        </button>
        <button className="delete" onClick={deleteItem}>
          DELETE
        </button>
      </div>
      {showModal && <Modal mode={'edit'} setShowModal={setShowModal} getData={getData} task={task} />}
    </li>
  );
};

export default ListItem;
